              key={item.id}
              onClick={() => setView(item.id as DashboardView)}
              className={`flex items-center gap-2 px-3 py-1.5 text-sm rounded-md transition-colors ${
                view === item.id
                  ? 'bg-zinc-800 text-white font-semibold'
